<?phpnamespace jobs;class Routes implements \as2\Routes{    public function getPage($route)    {        require '../Database.php';        $jobTable = new \as2\DatabaseTable($pdo, 'job', 'id');        $categoriesTable = new \as2\DatabaseTable($pdo, 'category', 'id');        $userTable = new \as2\DatabaseTable($pdo, 'user', 'id');        $applicantsTable = new \as2\DatabaseTable($pdo, 'applicants', 'id');        $admin = new \as2\DatabaseTable($pdo, 'admin', 'id');        $staffTable = new \as2\DatabaseTable($pdo, 'staff', 'id');        $enquiry = new \as2\DatabaseTable($pdo, 'enquiries', 'id');        $controllers = [];        $controllers['job'] = new \jobs\Controllers\Job($jobTable);        $controllers['category'] = new \jobs\Controllers\Category($categoriesTable);        $controllers['User'] = new \jobs\Controllers\User($userTable);        $controllers['Admin'] = new \jobs\Controllers\Admin($admin);        $controllers['Applicants'] = new \jobs\Controllers\Applicants($applicantsTable,$jobTable);        $controllers['Enquiry'] = new \jobs\Controllers\Enquiry($enquiry);        if ($route == '') {            $page = $controllers['job']->home();        } else {            list($controllerName, $functionName) = explode('/', $route);            if ($_SERVER['REQUEST_METHOD'] == 'POST') {                $functionName = $functionName . 'Submit';            }            $controller = $controllers[$controllerName];            $page = $controller->$functionName();        }        return $page;    }    public function getDefaultRoute()    {        return 'Job/home';    }}